import React from 'react';
import { Link } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { useLocation } from 'react-router-dom';

const Registration = () => {
    const location = useLocation();
    const user = location.state?.user || {};

    const initialValues = user.id
        ? {
            name: user.name,
            email: user.email,
            password: '',
            phone: user.phone,
            dob: user.dob,
            address: user.address,
        }
        : {
            name: '',
            email: '',
            password: '',
            phone: '',
            dob: '',
            address: '',
        };

    const validationSchema = Yup.object({
        name: Yup.string().required('Required'),
        email: Yup.string().email('Invalid email').required('Required'),
        password: user.id ? Yup.string() : Yup.string().min(6, 'Password must be at least 6 characters').required('Required'),
        phone: Yup.string().required('Required'),
        dob: Yup.string().required('Required'),
        address: Yup.string().required('Required'),
    });

    const handleSubmit = (values) => {
        console.log('Form values:', values);
    };

    return (
        <div className="flex justify-center items-center min-h-screen">
            <div className="w-full max-w-md p-6 bg-white rounded shadow-md">
                <h2 className="text-2xl font-semibold mb-4">Registration</h2>
                <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={handleSubmit}>
                    {() => (
                        <Form>
                            {/* ... rest of the form fields ... */}
                            <div className="mb-4">
                                <label htmlFor="password" className="block font-semibold mb-1">Password</label>
                                <Field
                                    type="password"
                                    id="password"
                                    name="password"
                                    className="w-full p-2 border rounded"
                                    disabled={user.id}
                                />
                                <ErrorMessage name="password" component="div" className="text-red-500" />
                            </div>
                            {/* ... rest of the form fields ... */}
                            <div className="flex justify-between items-center">
                                <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 focus:outline-none">Register</button>
                                <Link to="/signin" className="text-blue-500 hover:underline">Already have an account? Login</Link>
                            </div>
                        </Form>
                    )}
                </Formik>
            </div>
        </div>
    );
};

export default Registration;
